	private boolean isVariable(String str) {
		if (str == null){ return false;	}
		String firstChar = str.substring(0, 1);
		return firstChar.equals(firstChar.toUpperCase()); // Is uppercase -> Variable
	}

	/**
	 * Ajoute la variable v dans la table de variables locales si v n'existait pas avant
	 * @param f
	 * @param v
	 */
	public void varDeclaration3Addr(FunctionDef f, String v) {
		if(isVariable(v)){
			if (!f.alreadyExisting(v)) {
				if (!f.localVarAlreadyExisting(v)) {
					f.updateVars_local(v);
				}
			}
		}
	}

	private boolean isCallInAffect(EList<Expr> exprs) {
		boolean bool = false;
		Iterator<Expr> ite = exprs.iterator();
		while(ite.hasNext()) {
			Expr exp = ite.next();
			EObject obj = exp.getExpr();
			if(obj instanceof ExprSym) {
				if(isFunction(((ExprSym) obj).getArg1())) {
					NombreSortiesCall += listFunction.get(((ExprSym) obj).getArg1()).getOutputCount();
					nombreCallsAffect++;
					bool = true;
				}
			}
		}
		return bool;
	}
