	/**************************** Structures de contrôles 
	 * @throws WhileExceptions ***************/

	//while
	private void compile(While wh, FunctionDef f) throws WhileExceptions {
		String label = codeI.getEtiquette();
		codeI.nouvelleEtiquette();
		String cond = compile(wh.getExpr(), f); // condition
		if(cond !=null) {
			codeI.decl(cond);
		}
		codeI.finEtiquette();

		codeI.nouvelleEtiquette();
		Commands cmds = wh.getCmds();
		compile(cmds, f); //corps du while
		codeI.finEtiquette();
		codeI.whileLoop(label, codeI.getPreviousEtiquette());
	}

	//For
	private void compile(For fr, FunctionDef f) throws WhileExceptions {
		Object expr = fr.getExpr().getExpr();
		assert !((expr instanceof ExprAnd) || (expr instanceof ExprOr) || (expr instanceof ExprEq) || (expr instanceof ExprNot) ): "Pas d'expression booléenne comme condition pour le For";
		Expr exp = fr.getExpr();
		String label = codeI.getEtiquette();
		codeI.nouvelleEtiquette();
		String cond = compile(exp, f);
		if(cond != null) {
			codeI.decl(cond);
		}

		codeI.finEtiquette();

		codeI.nouvelleEtiquette();
		Commands cmds = fr.getCmds();
		compile(cmds, f);
		/// une petite partie bizare à revoir
		codeI.finEtiquette();
		codeI.forLoop(label, codeI.getPreviousEtiquette());

	}

	//Foreach
	private void compile(Foreach freach, FunctionDef f) throws WhileExceptions {
		Object exprr = freach.getExpr2().getExpr();
		assert !((exprr instanceof ExprAnd) || (exprr instanceof ExprOr) || (exprr instanceof ExprEq) || (exprr instanceof ExprNot) ): "Pas d'expression booléenne comme condition pour le Foreach";

		String var = freach.getVar();
		Expr expression = freach.getExpr2();


		String etiquetteexpr=codeI.getEtiquette();

		codeI.nouvelleEtiquette();
		String expr=compile(expression, f);


		codeI.finEtiquette();

		codeI.nouvelleEtiquette(); //Pour le corps de du foreach

		compile(freach.getCmd(),f);
		codeI.finEtiquette();
		codeI.forEachLoop(etiquetteexpr, codeI.getPreviousEtiquette(), var);
	}

	//If
	private void compile(If ifcmd, FunctionDef f) throws WhileExceptions {
		String label = codeI.getEtiquette();
		codeI.nouvelleEtiquette();
		String cond = compile(ifcmd.getExpr(), f);
		if(cond != null) {
			codeI.decl(cond);
		}
		codeI.finEtiquette();

		codeI.nouvelleEtiquette();
		Commands cmds1 = ifcmd.getCommands1();
		compile(cmds1, f);
		String label2 = codeI.getEtiquette();
		//else
		Commands cmds2 = ifcmd.getCommands2();
		if(cmds2 != null) {
			codeI.finEtiquette();
			codeI.nouvelleEtiquette();
			compile(cmds2, f);
			codeI.finEtiquette();
			codeI.ifElseCond(label, label2, codeI.getPreviousEtiquette());
		}
		else {
			codeI.finEtiquette();
			codeI.ifCond(label, codeI.getPreviousEtiquette());
		}
	}


